const { handler: listHandler } = require('./list');
const { dateHandler, TIME_FORMATS } = require('./date');
const { textHandler, RICH_TEXT_FORMATS } = require('./richtext');

const PARAM_TYPES = [
	{
		code: 'list',
		handler: listHandler
	},
	{
		code: 'string',
		handler: textHandler,
		formats: RICH_TEXT_FORMATS
	},
	{
		code: 'number'
	},
	{
		code: 'object'
	},
	{
		code: 'date',
		handler: dateHandler,
		formats: {
			time: TIME_FORMATS
		}
	},
	{
		code: 'datetime',
		handler: dateHandler,
		formats: {
			time: TIME_FORMATS
		}
	}
];

const PARAM_TYPES_MAP = PARAM_TYPES.reduce((obj, paramType) => ({ ...obj, [paramType.code.toUpperCase()]: paramType }), {});

module.exports = PARAM_TYPES_MAP;
