const mockEvent = require('./default_app/mock_event');

function MockEventBuilder() {
	this.event = mockEvent;

	this.getDefaultEvent = function () {
		return mockEvent;
	};

	this.setMethod = function (method) {
		this.event.method = method;
		return this;
	};

	this.setOptions = function (options) {
		this.event.options = options;
		return this;
	};

	this.setAcMode = function (boolean) {
		this.event.options.acMode = boolean;
		return this;
	};

	this.setDataInput = function (input) {
		this.event.data.input = input;
		return this;
	};

	this.setPreviousVars = function (previousVars) {
		this.event.data.previousVars = previousVars;
		return this;
	};

	this.setTestResponse = function (testResponse) {
		this.event.data.testResponse = testResponse;
		return this;
	};

	this.build = function () {
		return this.event;
	};
}

module.exports = MockEventBuilder;
