const mockContextMapping = require('./default_app/mock_context_mapping');

function MockContextMappingBuilder() {
	this.contextMapping = mockContextMapping;

	this.getDefaultContextMapping = function () {
		return mockContextMapping;
	};

	this.setKey = function (key) {
		this.contextMapping.key = key;
		return this;
	};

	this.setURLRegex = function (urlRegex) {
		this.contextMapping.url_regex = urlRegex;
		return this;
	};

	this.setAction = function (actionKey) {
		this.contextMapping.action = actionKey;
		return this;
	};

	this.setParams = function (params) {
		this.contextMapping.params = params;
		return this;
	};

	this.build = function () {
		return this.contextMapping;
	};
}

module.exports = MockContextMappingBuilder;
